mapper.select:
select

from sb_stowage


             and stowage_time >= #{input.beginTime,jdbcType=TIMESTAMP}


             and stowage_time <= #{input.endTime,jdbcType=TIMESTAMP}


            AND plate_number like concat('%',trim(#{input.plateNumber,jdbcType=VARCHAR}),'%')


            AND driver_name like concat('%',trim(#{input.driverName,jdbcType=VARCHAR}),'%')


            AND status = #{input.status,jdbcType=INTEGER}


            AND alloc_center_id = #{input.allocCenterId,jdbcType=BIGINT}


            AND lsp_id = #{input.lspId,jdbcType=BIGINT}


order by case status when '5' then 1 when '0' then 2 when '10' then 3 else 4 end asc
