select w.id as id,w.alloc_centre_id as allocCentreId,w.alloc_centre as allocCentre,w.region_id as regionId,w.region as region,
w.customer_name as customerName,w.merchant_status as merchantStatus,w.curr_user as currUser,w.curr_user_id as currUserId,
if(w.shipments_status is null,w.status,w.shipments_status) as status,min(b.operation_time) as operationTime,
if(count(b.id)>0,count(b.id),0) as waybillCount,
if(sum(f.all_fee)>0,sum(f.all_fee),0) as allFee,
if(count(d.id)>0,count(d.id),0) as damagerCount
from mms_outer_customer w
left join sb_customer c on w.customer_name = c.customer_account


        left join (select * from sb_waybill where date_format(operation_time,'%Y-%m')=date_format(now(),'%Y-%m') ) b on b.shipper_customer_id = c.id


        inner join (select * from sb_waybill where date_format(operation_time,'%Y-%m')=date_format(#{query.operationTime},'%Y-%m') ) b on b.shipper_customer_id = c.id


left join (select * from sb_damager group by waybill_id) d on b.id=d.waybill_id
left join sb_waybill_fee f on b.id=f.waybill_id


    and w.region_id in

        #{item}



    and w.alloc_centre_id = #{query.allocCentreId}


    and w.region_id = #{query.regionId}


    and w.status = #{query.status}


    and w.merchant_status = #{query.merchantStatus}


    and w.logistics_demand = #{query.logisticsDemand}


    and w.customer_name like concat('%',trim(#{query.customerName}),'%')


  and w.service_life >= now()


  and

    w.curr_user like concat('%',trim(#{tag}),'%')



group by w.id
order by w.created_time desc
